mapper.select:
SELECT count(order_details.id_game) FROM orders inner join order_details on orders.id_order=order_details.id_order WHERE order_details.id_game in
#{item}
GROUP BY month(order_details.id_game)

SELECT count(order_details.id_game) as sells, sum(order_details.price) as earnings, games.name as game FROM orders inner join order_details on orders.id_order=order_details.id_order inner join games on games.id_game=order_details.id_game WHERE order_details.id_game in
#{item}
group by order_details.id_game

SELECT users.id_country FROM orders inner join order_details on orders.id_order=order_details.id_order inner join users on orders.id_user=users.id_user WHERE order_details.id_game in
#{item}
group by users.id_country

SELECT countries.name as country, count(orders.id_user) as sells, sum(order_details.price) as earnings FROM orders inner join order_details on orders.id_order=order_details.id_order inner join users on orders.id_user=users.id_user inner join countries on users.id_country=countries.id_country WHERE order_details.id_game in
#{item}
and countries.id_country in
#{item}
group by users.id_country

SELECT count(order_details.id_game) as sells, sum(order_details.price) as earnings, month(order_details.tx_date) as month, year(order_details.tx_date) as year FROM orders inner join order_details on orders.id_order=order_details.id_order WHERE order_details.id_game in
#{item}
and month(order_details.tx_date) in
#{item}
GROUP BY month(order_details.tx_date), year(order_details.tx_date)
